mapper.insert:
insert  into  website_columns(
    columnName,
    columnLevel,
    isJump

    ,columnLink


    ,columnParent


    ,remarks

    )
values (
    #{columnName},
    #{columnLevel},
    #{isJump}

    ,#{columnLink}


    ,#{columnParent}


    ,#{remarks}

)

mapper.update:
update  website_columns

    columnName=#{columnName},
    remarks=#{remarks},
    isJump=#{isJump},
    columnLink=#{columnLink}

where columnId=#{columnId}

update website_columns
set
  isShow=#{isShow}
where columnId=#{columnId} or columnParent=#{columnId}
