mapper.insert:
insert  into  website_columns(
    columnName,
    columnLevel

    ,columnParent

    )
values (
    #{columnName},
    #{columnLevel}

    ,#{columnParent}

)

mapper.update:
update  website_columns
set
  columnName=#{columnName},
  remarks=#{remarks}
where columnId=#{columnId}

update website_columns
set
  isShow=#{isShow}
where columnId=#{columnId} or columnParent=#{columnId}
