mapper.select:
SELECT a.consulId,b.columnName,a.title,a.consuType,a.consuClass,a.consuPlace FROM consultation_info a,website_columns b
WHERE a.isDel = '0' and a.columnId=b.columnId

    and a.title like CONCAT('%',#{title},'%')


    and b.columnName like CONCAT('%',#{columnName},'%')


    and a.consuPlace=#{consuPlace}


    and a.columnId=#{columnId}

mapper.insert:
insert into consultation_info( columnId, title, consuDesc, consuType, consuPlace, consuClass, context, remarks, images
,consuLink
) values ( #{columnId}, #{title}, #{consuDesc}, #{consuType}, #{consuPlace}, #{consuClass}, #{context,jdbcType=BLOB}, #{remarks}, #{images}
,#{consuLink}
)

mapper.update:
update consultation_info
columnId=#{columnId}, title=#{title}, consuDesc=#{consuDesc}, consuType=#{consuType}, consuPlace=#{consuPlace}, consuClass=#{consuClass}, context=#{context,jdbcType=BLOB}, remarks=#{remarks}, images=#{images}, consuLink=#{consuLink}
where consulId=#{consulId}
